import React, { Component } from 'react';
import {BrowserRouter as Router, Route, Link, NavLink} from 'react-router-dom';
import SignUpForm from './SignUpForm';
import SignInForm from './SignInForm';
import './../styles/App.css';

class App extends Component {
  state = {
    loggedIn: false
  };

  // componentDidMount() {
  //     // Call our fetch function below once the component mounts
  //   this.callBackendAPI()
  //     .then(res => this.setState({ data: res.express }))
  //     .catch(err => console.log(err));
  // }
  //   // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  // callBackendAPI = async () => {
  //   const response = await fetch('/express_');
  //   const body = await response.json();

  //   if (response.status !== 200) {
  //     throw Error(body.message) 
  //   }
  //   return body;
  // };

  render() {
    return (
      <Router>

        <div className="App">

          {/* This is the left side (description) */}
          <div className="Background">
            <div className="Box">
              <h1> S<span className="normalFontWeight">tudent</span></h1> 
              <h1> T<span className="normalFontWeight">eacher</span></h1> 
              <h1> R<span className="normalFontWeight">elationship</span></h1> 
              <h1> M<span className="normalFontWeight">anagement</span></h1> 
              <h1> P<span className="normalFontWeight">latform</span></h1> 
            </div>    
          </div>
          {/* This is the right side (form) */}
          <div className="formView">

            <div className="PageSwitcher">
              <NavLink to="/sign-in" activeClassName= "PageSwitchActive" className="PageSwitch"> Sign In </NavLink>
              <NavLink exact to="/" activeClassName= "PageSwitchActive" className="PageSwitch"> Sign Up </NavLink>
            </div>

            <div className="Title">
              <NavLink to="/sign-in" activeClassName = "TitleLinkUnderline" className="TitleLink">Sign In</NavLink> or 
              <NavLink exact to="/" activeClassName="TitleLinkUnderline"className="TitleLink">Sign Up</NavLink>
            </div>

            <Route exact path="/" component={SignUpForm}> </Route>

          <Route path="/sign-in" component={SignInForm}> </Route>


        </div>


        </div>
      </Router>
    );
  }
}

export default App;
