import React, {Component} from 'react';
import axios from 'axios';
class SignUpForm extends Component {

    constructor(props) {
      super(props);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.handleIdentity = this.handleIdentity.bind(this);
      this.state = {
        identity: ""
      }
    }

    handleIdentity(event) {
      if (event.target.value == 'studentBtn') {
        this.setState({
          identity: 'S'
        });
      }
      if (event.target.value == 'teacherBtn') {
        this.setState({
          identity: 'T'
        });
      }

    }

    handleSubmit(event) {
      event.preventDefault();
      const data = {
        firstName: this.refs.firstName.value,
        lastName: this.refs.lastName.value,
        username: this.refs.username.value,
        password: this.refs.password.value,
        identity: this.state.identity
      };

      this.signup(data);

      // Call our fetch function below once the component mounts
      // this.callBackendAPI(data)
      // .then(res => this.setState({ data: res.express }))
      // .catch(err => console.log(err));
    }

    signup = data => {
      return axios.post('/signup', {
        firstName: data.firstName,
        lastName: data.lastName,
        username: data.username,
        password: data.password,
        identity: data.identity
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
    }

    render() {
        return (
            <div className="FormCenter">
            <form onSubmit={this.handleSubmit}>
              <div className="FieldContainer">
                <label className="FieldLabel" htmlFor="name">First Name</label>
                <input type="text" id="firstName" className="FieldInput" placeholder="Please enter your first name" ref="firstName" required/>
              </div>

              <div className="FieldContainer">
                <label className="FieldLabel" htmlFor="name">Last Name</label>
                <input type="text" id="lastName" className="FieldInput" placeholder="Please enter your last name" ref="lastName" required/>
              </div>

              <div className="FieldContainer">
                <label className="FieldLabel" htmlFor="username">Username</label>
                <input type="text" id="username" className="FieldInput" placeholder="Please enter your username" ref="username" required/>
              </div>

              <div className="FieldContainer">
                <label className="FieldLabel" htmlFor="password">Password</label>
                <input type="password" id="password" className="FieldInput" placeholder="Please enter your password" ref="password" required/>
              </div>

              <div className="FieldContainer">
                <label className="FieldLabel" htmlFor="identity">Are you a student or a teacher?</label>
                  <input type="radio" name="identity" className="identityButton" value="studentBtn" onClick={this.handleIdentity} />
                  <label className="identityButton"> Student </label>
                  <input type="radio" name="identity" className="identityButton" value="teacherBtn" onClick={this.handleIdentity} />
                  <label className="identityButton"> Teacher </label>
              </div>

              <div className="FieldContainer">
                <button className="FieldButton mr-25"> Sign Up </button>
              </div>

            </form>
          </div>
        );
    }
}

export default SignUpForm;
