import React, { Component } from 'react';
import CreateClass from './CreateClass';
import AddGrade from './AddGrade';
import JoinClass from './JoinClass';
import Schedule from './Schedule';
import Roster from './Roster';
import GetGradesStudent from './GetGradesStudent';
import GetGradesTeacher from './GetGradesTeacher';
import './../styles/App.css';

class Home extends Component {

    render() {
        return (
            <React.Fragment>
                <h1>Welcome {this.props.userdata.username}</h1>
                <Schedule userdata = {this.props.userdata}/>
                {this.props.userdata.identity === 'T' ? <CreateClass userdata = {this.props.userdata}/> : null}
                {this.props.userdata.identity === 'S' ? <JoinClass userdata = {this.props.userdata}/> : null}
                <Roster userdata = {this.props.userdata}/>
                {this.props.userdata.identity === 'T' ? <GetGradesTeacher userdata = {this.props.userdata}/> : <GetGradesStudent userdata = {this.props.userdata}/>}
                {this.props.userdata.identity === 'T' ? <AddGrade userdata = {this.props.userdata}/> : null}
                <script src="/socket.io/socket.io.js"></script>


                <div>
                    <form>
                        <input id="chat-text" type="text" placeholder="Let's chat. Type a message..."/>
                        <button id="chat-submit" type="submit"></button>
                    </form>
                </div>
            </React.Fragment>
        )
    }
}

export default Home;
