import React, {Component} from 'react';
import axios from 'axios';

class SignInForm extends Component {

    constructor(props) {
      super(props);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
      event.preventDefault();
      const data = {
        username: this.refs.username.value,
        password: this.refs.password.value,
      }

      this.signin(data);
    }

    signin = data => {
      return axios.post('/signin', {
        username: data.username,
        password: data.password,
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
    }

    render() {
        return (
            <div className="FormCenter">
            <form  onSubmit={this.handleSubmit}>

              <div className="FieldContainer">
                <label className="FieldLabel" htmlFor="username">Username</label>
                <input type="text" id="username" className="FieldInput" placeholder="Please enter your username" ref="username"/>
              </div>

              <div className="FieldContainer">
                <label className="FieldLabel" htmlFor="password">Password</label>
                <input type="password" id="password" className="FieldInput" placeholder="Please enter your password" ref="password"/>
              </div>


              <div className="FieldContainer">
                <button className="FieldButton mr-25"> Sign In </button> 
              </div>

            </form>
          </div>
        );
    }
}

export default SignInForm;
